// Utility functions for search
const searchUtils = {
  // Get all projects across categories
  getAllProjects: function() {
    return [
      ...scienceProjectsData.primary,
      ...scienceProjectsData.elementary,
      ...scienceProjectsData.intermediate,
      ...scienceProjectsData.senior
    ];
  },
  
  // Search projects by query
  searchProjects: function(query, category = null) {
    const allProjects = category ? scienceProjectsData[category] : this.getAllProjects();
    const searchTerms = query.toLowerCase().split(' ');
    
    return allProjects.filter(project => {
      const searchText = `${project.title} ${project.description} ${project.subject} ${project.materials}`.toLowerCase();
      return searchTerms.every(term => searchText.includes(term));
    });
  },
  
  // Filter projects by criteria
  filterProjects: function(projects, filters) {
    return projects.filter(project => {
      // Subject filter
      if (filters.subject && filters.subject.length > 0) {
        if (!filters.subject.includes(project.subject)) return false;
      }
      
      // Difficulty filter
      if (filters.difficulty && filters.difficulty.length > 0) {
        if (!filters.difficulty.includes(project.difficulty)) return false;
      }
      
      // Materials filter
      if (filters.materials && filters.materials.length > 0) {
        if (!filters.materials.includes(project.materials)) return false;
      }
      
      // Category filter
      if (filters.category && filters.category.length > 0) {
        if (!filters.category.includes(project.category)) return false;
      }
      
      // Popular filter
      if (filters.popular && !project.popular) return false;
      
      return true;
    });
  },
  
  // Sort projects
  sortProjects: function(projects, sortBy) {
    return [...projects].sort((a, b) => {
      switch (sortBy) {
        case 'name':
          return a.title.localeCompare(b.title);
        case 'difficulty':
          const difficultyOrder = { easy: 1, medium: 2, hard: 3 };
          return difficultyOrder[a.difficulty] - difficultyOrder[b.difficulty];
        case 'subject':
          return a.subject.localeCompare(b.subject);
        case 'popular':
          return (b.popular ? 1 : 0) - (a.popular ? 1 : 0);
        default:
          return 0;
      }
    });
  },
  
  // Get projects by category
  getProjectsByCategory: function(category) {
    return scienceProjectsData[category] || [];
  },
  
  // Get popular projects
  getPopularProjects: function(limit = 10) {
    return this.getAllProjects().filter(p => p.popular).slice(0, limit);
  },
  
  // Get random projects
  getRandomProjects: function(limit = 6) {
    const allProjects = this.getAllProjects();
    const shuffled = [...allProjects].sort(() => 0.5 - Math.random());
    return shuffled.slice(0, limit);
  }
};

// Export for use in other scripts
if (typeof module !== 'undefined' && module.exports) {
  module.exports = { scienceProjectsData, searchUtils };
}



